import React, { useState } from "react";
import DevicePicker from "./DevicePicker";
import DaysPicker from "./DaysPicker";
import AddClassModal from "./AddClassModal";

const headerHeight = 40; // px for the row of day labels
const initialHours = Array.from({ length: 13 }, (_, i) => 7 + i); // [7,8,...,19]

function App() {
  const [device, setDevice] = useState(null);
  const [visibleHours, setVisibleHours] = useState(initialHours);
  const [selectedDays, setSelectedDays] = useState(["M0", "T1", "W2", "T3", "F4"]);
  const [showModal, setShowModal] = useState(false);
  const [classes, setClasses] = useState([]);

  const dayLabels = [
    { label: "M", name: "Monday" },
    { label: "T", name: "Tuesday" },
    { label: "W", name: "Wednesday" },
    { label: "T", name: "Thursday" },
    { label: "F", name: "Friday" },
    { label: "S", name: "Saturday" },
    { label: "S", name: "Sunday" }
  ];

  function toMins(t) {
    let [h, m] = t.split(":").map(Number);
    return h * 60 + m;
  }

  function canDeleteHour(hour) {
    const hourStart = hour * 60;
    const hourEnd = (hour + 1) * 60;
    return !classes.some(c => {
      const cStart = toMins(c.startTime);
      const cEnd = toMins(c.endTime);
      return c.days.some(d => selectedDays.includes(d)) &&
        cStart < hourEnd && cEnd > hourStart;
    });
  }

  function addHour(hour) {
    const newHours = [...visibleHours, hour].sort((a, b) => a - b);
    setVisibleHours(newHours);
  }

  function removeHour(hour) {
    setVisibleHours(visibleHours.filter(h => h !== hour));
  }

  const missingHours = initialHours.filter(h => !visibleHours.includes(h));
  const sortedHours = [...visibleHours].sort((a, b) => a - b);

  const gridHeight = Math.round(device ? device.height / 3 : 700);
  const totalRows = sortedHours.length;
  const gridDrawHeight = gridHeight - headerHeight;
  const firstHour = sortedHours[0];
  const lastHour = sortedHours[totalRows - 1];

  function timeToY(mins) {
    const minutesSinceStart = mins - firstHour * 60;
    const totalMinutes = (lastHour + 1 - firstHour) * 60;
    return headerHeight + (minutesSinceStart / totalMinutes) * gridDrawHeight;
  }

  function cellsForDay(day) {
    return classes
      .filter(c => c.days.includes(day))
      .sort((a, b) => toMins(a.startTime) - toMins(b.startTime));
  }

  function checkOverlap(newClass) {
    return classes.some(c =>
      c.days.some(d => newClass.days.includes(d)) &&
      !(
        newClass.endTime <= c.startTime ||
        newClass.startTime >= c.endTime
      )
    );
  }

  function handleAddClass(newClass) {
    if (checkOverlap(newClass)) {
      alert("Overlapping class detected. Please adjust the time or days.");
      return;
    }
    setClasses([...classes, newClass]);
    setShowModal(false);
  }

  return (
    <div className="min-h-screen flex flex-col items-center bg-gray-100">
      <h1 className="text-2xl font-bold mt-6 mb-4">imey-scheduler</h1>
      <DevicePicker onSelect={setDevice} />
      {device && (
        <div className="mt-8 flex flex-col items-center">
          <DaysPicker selectedDays={selectedDays} setSelectedDays={setSelectedDays} />

          <button
            className="mb-2 px-4 py-2 bg-green-600 text-white rounded"
            onClick={() => setShowModal(true)}
          >
            + Add Class
          </button>

          {/* Add/Remove hour row controls */}
          <div className="mb-2 flex flex-wrap items-center gap-2">
            <span className="mr-2 text-sm font-medium">Manage time rows:</span>
            {sortedHours.map(hour => (
              <button
                key={hour}
                onClick={() => canDeleteHour(hour) && removeHour(hour)}
                className={`text-xs px-2 py-1 rounded border ${canDeleteHour(hour) ? "bg-red-100 hover:bg-red-200 text-red-600" : "bg-gray-100 text-gray-400 cursor-not-allowed"}`}
                disabled={!canDeleteHour(hour)}
                title={canDeleteHour(hour) ? "Delete this hour row" : "Cannot delete (row contains class)"}
                style={{ minWidth: 40 }}
              >
                {hour < 10 ? "0" + hour : hour}:00 ✕
              </button>
            ))}
            {missingHours.length > 0 && (
              <select
                className="ml-4 text-xs border p-1 rounded"
                onChange={e => {
                  const val = Number(e.target.value);
                  if (val) addHour(val);
                  e.target.value = "";
                }}
                defaultValue=""
              >
                <option value="" disabled>
                  + Add row...
                </option>
                {missingHours.map(h => (
                  <option key={h} value={h}>
                    {h < 10 ? "0" + h : h}:00
                  </option>
                ))}
              </select>
            )}
          </div>

          {/* Table grid */}
          <div
            style={{
              width: `${Math.round(device.width / 3)}px`,
              height: `${gridHeight}px`,
              border: "2px solid #aaa",
              borderRadius: "20px",
              background: "#fff",
              position: "relative",
              boxShadow: "0 2px 12px rgba(0,0,0,0.07)",
              overflow: "hidden",
              display: "flex",
              flexDirection: "row"
            }}
          >
            {/* Time axis on left */}
            <div
              style={{
                width: 40,
                background: "#fff",
                zIndex: 2,
                height: gridHeight
              }}
            >
              <div style={{ height: headerHeight, width: "100%" }}></div>
              {/* Each row for each hour */}
              {sortedHours.map((hour, idx) => (
                <div
                  key={hour}
                  style={{
                    height: `${gridDrawHeight / totalRows}px`,
                    width: "100%",
                    display: "flex",
                    alignItems: "flex-start",
                    justifyContent: "center",
                    position: "relative"
                  }}
                >
                  <span
                    style={{
                      fontSize: 13,
                      color: "#a5a5a5",
                      position: "absolute",
                      top: 2,
                      left: 0,
                      width: "100%",
                      textAlign: "center"
                    }}
                  >
                    {hour < 10 ? "0" + hour : hour}
                  </span>
                </div>
              ))}
            </div>
            {/* Table for timetable */}
            <div
              style={{
                flex: 1,
                display: "flex",
                flexDirection: "column",
                height: "100%"
              }}
            >
              {/* Day label row */}
              <div style={{ display: "flex", height: headerHeight }}>
                {selectedDays.map((day, dayIdx) => (
                  <div
                    key={day}
                    style={{
                      flex: 1,
                      fontWeight: "bold",
                      fontSize: 22,
                      color: "#b5b5b5",
                      textAlign: "center",
                      lineHeight: `${headerHeight}px`,
                      background: "#fff"
                    }}
                  >
                    {dayLabels[parseInt(day.slice(-1))].label}
                  </div>
                ))}
              </div>
              {/* Hour rows */}
              {sortedHours.map((hour, hourIdx) => (
                <div
                  key={hour}
                  style={{
                    display: "flex",
                    height: `${gridDrawHeight / totalRows}px`,
                    borderTop: "0px solid #eee" // invisible line
                  }}
                >
                  {selectedDays.map((day, dayIdx) => {
                    // For this cell, is there a class running during this hour slot?
                    // Only render a block for the overlap portion.
                    const blocks = cellsForDay(day).filter(c => {
                      const cStart = toMins(c.startTime);
                      const cEnd = toMins(c.endTime);
                      const hourStart = hour * 60;
                      const hourEnd = (hour + 1) * 60;
                      return cEnd > hourStart && cStart < hourEnd;
                    });

                    if (blocks.length === 0) {
                      return (
                        <div key={dayIdx} style={{ flex: 1, position: "relative" }}></div>
                      );
                    }

                    // For each overlapping class, render the part of it in this hour
                    return (
                      <div key={dayIdx} style={{ flex: 1, position: "relative", display: "flex", flexDirection: "column", height: "100%" }}>
                        {blocks.map((c, i) => {
                          const cStart = toMins(c.startTime);
                          const cEnd = toMins(c.endTime);
                          const hourStart = hour * 60;
                          const hourEnd = (hour + 1) * 60;
                          // Find the portion of the class in this hour (for top and bottom)
                          const blockStart = Math.max(cStart, hourStart);
                          const blockEnd = Math.min(cEnd, hourEnd);
                          const totalHourMinutes = hourEnd - hourStart;
                          const topPct = ((blockStart - hourStart) / totalHourMinutes) * 100;
                          const heightPct = ((blockEnd - blockStart) / totalHourMinutes) * 100;

                          return (
                            <div
                              key={i}
                              style={{
                                position: "absolute",
                                left: 2,
                                right: 2,
                                top: `${topPct}%`,
                                height: `${heightPct}%`,
                                background: c.cellColor,
                                opacity: 1,
                                borderRadius: 0,
                                color: c.textColor,
                                fontSize: c.textSize,
                                fontWeight: 600,
                                boxShadow: "none",
                                padding: 4,
                                display: "flex",
                                flexDirection: "column",
                                justifyContent: "space-between",
                                overflow: "hidden"
                              }}
                            >
                              {/* Only show start time if this is the first block of the class */}
                              {blockStart === cStart && (
                                <div style={{ fontSize: 11, fontWeight: 500 }}>
                                  {c.startTime}
                                </div>
                              )}
                              <div
                                style={{
                                  flex: 1,
                                  textAlign: "center",
                                  overflow: "hidden",
                                  textOverflow: "ellipsis",
                                  whiteSpace: "pre-line",
                                  wordBreak: "break-word",
                                  padding: 2,
                                  display: "flex",
                                  flexDirection: "column",
                                  justifyContent: "center"
                                }}
                              >
                                <div style={{
                                  fontWeight: 700,
                                  overflow: "hidden",
                                  textOverflow: "ellipsis",
                                  whiteSpace: "pre-line",
                                  wordBreak: "break-word",
                                  fontSize: c.textSize
                                }}>{c.className}</div>
                                {c.location && (
                                  <div style={{
                                    fontSize: c.textSize,
                                    overflow: "hidden",
                                    textOverflow: "ellipsis",
                                    whiteSpace: "pre-line",
                                    wordBreak: "break-word"
                                  }}>{c.location}</div>
                                )}
                              </div>
                              {/* Only show end time if this is the last block of the class */}
                              {blockEnd === cEnd && (
                                <div style={{ fontSize: 11, textAlign: "right" }}>
                                  {c.endTime}
                                </div>
                              )}
                            </div>
                          );
                        })}
                      </div>
                    );
                  })}
                </div>
              ))}
            </div>
          </div>
          <div className="mt-2 text-xs text-gray-400">
            (Preview is scaled down for your screen. Final export will be full resolution.)
          </div>
        </div>
      )}

      {/* Add Class Modal */}
      <AddClassModal
        visible={showModal}
        onClose={() => setShowModal(false)}
        onAdd={handleAddClass}
        days={selectedDays}
      />
    </div>
  );
}

export default App;
