import React, { useState } from "react";
import DevicePicker from "./DevicePicker";
import DaysPicker from "./DaysPicker";
import AddClassModal from "./AddClassModal";

function App() {
  const [device, setDevice] = useState(null);
  const [selectedDays, setSelectedDays] = useState(["M0", "T1", "W2", "T3", "F4"]);
  const [showModal, setShowModal] = useState(false);
  const [classes, setClasses] = useState([]);

  const dayLabels = [
    { label: "M", name: "Monday" },
    { label: "T", name: "Tuesday" },
    { label: "W", name: "Wednesday" },
    { label: "T", name: "Thursday" },
    { label: "F", name: "Friday" },
    { label: "S", name: "Saturday" },
    { label: "S", name: "Sunday" }
  ];

  function toMins(t) {
    let [h, m] = t.split(":").map(Number);
    return h * 60 + m;
  }

  // Find min/max for axis based on classes
  let minTime = 9 * 60, maxTime = 17 * 60;
  if (classes.length) {
    const allTimes = classes.flatMap(c => [toMins(c.startTime), toMins(c.endTime)]);
    minTime = Math.min(...allTimes);
    maxTime = Math.max(...allTimes);
    minTime = Math.floor(minTime / 60) * 60;
    maxTime = Math.ceil(maxTime / 60) * 60;
  }

  // Time labels
  const timeLabels = [];
  for (let t = minTime; t <= maxTime; t += 60) {
    let h = Math.floor(t / 60);
    let label = `${h < 10 ? "0" : ""}${h}`;
    timeLabels.push({ mins: t, label });
  }

  function cellsForDay(day) {
    return classes
      .filter(c => c.days.includes(day))
      .sort((a, b) => toMins(a.startTime) - toMins(b.startTime));
  }

  // Add this near the top, outside your component:
  const headerHeight = 40; // px for the days row

  // In your component (replace gridTopPad with headerHeight):
  const gridHeight = Math.round(device ? device.height / 3 : 700);
  const totalMins = maxTime - minTime;
  const gridDrawHeight = gridHeight - headerHeight;

  // ...then update timeToY:
  function timeToY(mins) {
    return headerHeight + ((mins - minTime) / totalMins) * gridDrawHeight;
  }

  return (
    <div className="min-h-screen flex flex-col items-center bg-gray-100">
      <h1 className="text-2xl font-bold mt-6 mb-4">imey-scheduler</h1>
      <DevicePicker onSelect={setDevice} />
      {device && (
        <div className="mt-8 flex flex-col items-center">
          <DaysPicker selectedDays={selectedDays} setSelectedDays={setSelectedDays} />

          <button
            className="mb-4 px-4 py-2 bg-green-600 text-white rounded"
            onClick={() => setShowModal(true)}
          >
            + Add Class
          </button>

          <div
            style={{
              width: `${Math.round(device.width / 3)}px`,
              height: `${gridHeight}px`,
              border: "2px solid #aaa",
              borderRadius: "20px",
              background: "#fff",
              position: "relative",
              boxShadow: "0 2px 12px rgba(0,0,0,0.07)",
              overflow: "hidden",
              display: "flex",
              flexDirection: "row"
            }}
          >
            {/* Time axis on left */}
            <div
              style={{
                width: 40,
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                fontSize: 13,
                color: "#a5a5a5",
                position: "relative",
                background: "#fff",
                zIndex: 2,
                height: gridHeight
              }}
            >
              {/* Row for day label placeholder */}
              <div style={{ height: headerHeight, width: "100%" }}></div>
              {/* Dynamically spaced time labels */}
              {timeLabels.map((t, idx) => (
                <div
                  key={t.label}
                  style={{
                    position: "absolute",
                    top: timeToY(t.mins),
                    left: 0,
                    width: "100%",
                    textAlign: "center",
                    transform: "translateY(-50%)"
                  }}
                >
                  {t.label}
                </div>
              ))}
            </div>
            {/* Columns for days */}
            <div
              style={{
                flex: 1,
                display: "flex",
                flexDirection: "row",
                height: "100%"
              }}
            >
              {selectedDays.map((day, dayIdx) => (
                <div
                  key={day}
                  style={{
                    flex: 1,
                    position: "relative",
                    height: "100%",
                    overflow: "visible"
                  }}
                >
                  {/* Day label at top (floating, not inside grid) */}
                  <div
                    style={{
                      fontWeight: "bold",
                      fontSize: 22,
                      color: "#b5b5b5",
                      textAlign: "center",
                      height: headerHeight,
                      lineHeight: `${headerHeight}px`,
                      background: "#fff",
                      zIndex: 1
                    }}
                  >
                    {dayLabels[parseInt(day.slice(-1))].label}
                  </div>
                  {/* Render classes in this column */}
                  {cellsForDay(day).map((c, idx) => {
                    const startY = timeToY(toMins(c.startTime));
                    const endY = timeToY(toMins(c.endTime));
                    const height = Math.max(16, endY - startY);
                    return (
                      <div
                        key={idx}
                        style={{
                          position: "absolute",
                          left: 0, right: 0,
                          top: startY,
                          height,
                          background: c.cellColor,
                          opacity: 1,
                          borderRadius: 0,
                          color: c.textColor,
                          fontSize: c.textSize,
                          fontWeight: 600,
                          boxShadow: "none",
                          padding: 4,
                          display: "flex",
                          flexDirection: "column",
                          justifyContent: "space-between",
                          cursor: "pointer",
                          zIndex: 2,
                          overflow: "hidden"
                        }}
                      >
                        <div style={{ fontSize: 11, fontWeight: 500 }}>
                          {c.startTime}
                        </div>
                        <div
                          style={{
                            flex: 1,
                            textAlign: "center",
                            overflow: "hidden",
                            textOverflow: "ellipsis",
                            whiteSpace: "pre-line",
                            wordBreak: "break-word",
                            padding: 2,
                            display: "flex",
                            flexDirection: "column",
                            justifyContent: "center"
                          }}
                        >
                          <div style={{
                            fontWeight: 700,
                            overflow: "hidden",
                            textOverflow: "ellipsis",
                            whiteSpace: "pre-line",
                            wordBreak: "break-word",
                            fontSize: c.textSize
                          }}>{c.className}</div>
                          {c.location && (
                            <div style={{
                              fontSize: c.textSize,
                              overflow: "hidden",
                              textOverflow: "ellipsis",
                              whiteSpace: "pre-line",
                              wordBreak: "break-word"
                            }}>{c.location}</div>
                          )}
                        </div>
                        <div style={{ fontSize: 11, textAlign: "right" }}>
                          {c.endTime}
                        </div>
                      </div>
                    );
                  })}
                </div>
              ))}
            </div>
          </div>
          <div className="mt-2 text-xs text-gray-400">
            (Preview is scaled down for your screen. Final export will be full resolution.)
          </div>
        </div>
      )}

      {/* Add Class Modal */}
      <AddClassModal
        visible={showModal}
        onClose={() => setShowModal(false)}
        onAdd={c => setClasses([...classes, c])}
        days={selectedDays}
      />
    </div>
  );
}

export default App;
