import React, { useRef, useState, useEffect } from "react";
import DevicePicker from "./DevicePicker";
import DaysPicker from "./DaysPicker";
import AddClassModal from "./AddClassModal";
import EditClassModal from "./EditClassModal";
import SettingsModal from "./SettingsModal";
import BackgroundCropperModal from "./BackgroundCropperModal";
import { toPng } from "html-to-image";
import { saveAs } from "file-saver";

function hexToRgba(hex, alpha = 0.5) {
  const [r, g, b] = hex.match(/\w\w/g).map(v => parseInt(v, 16));
  return `rgba(${r}, ${g}, ${b}, ${alpha})`;
}

const headerHeight = 40;
const initialHours = Array.from({ length: 13 }, (_, i) => 7 + i);

function App() {
  const [device, setDevice] = useState(null);
  const [visibleHours, setVisibleHours] = useState(initialHours);
  const [selectedDays, setSelectedDays] = useState(["M0", "T1", "W2", "T3", "F4"]);
  const [showModal, setShowModal] = useState(false);
  const [classes, setClasses] = useState([]);
  const [showEdit, setShowEdit] = useState(false);
  const [editClass, setEditClass] = useState(null);
  const [showCropper, setShowCropper] = useState(false);
  const [rawImage, setRawImage] = useState(null);
  const [backgroundImage, setBackgroundImage] = useState(null);

  const [showSettings, setShowSettings] = useState(false);
  const [settings, setSettings] = useState({
    cellOpacity: 0.5,
    cellTextSize: 11,
    cellTextColor: "#ffffff",
    cellTextShadow: true,
    contentBold: false,
    timeBold: false,
    dayTimeTextSize: 13,
    dayTimeTextColor: "#ffffff",
  });
  const [previewSettings, setPreviewSettings] = useState(settings);
  useEffect(() => {
    if (showSettings) setPreviewSettings(settings);
  }, [showSettings, settings]);
  const activeSettings = showSettings ? previewSettings : settings;

  const inputRef = useRef(null);
  const timetableRef = useRef(null);

  const dayLabels = [
    { label: "M", name: "Monday" },
    { label: "T", name: "Tuesday" },
    { label: "W", name: "Wednesday" },
    { label: "T", name: "Thursday" },
    { label: "F", name: "Friday" },
    { label: "S", name: "Saturday" },
    { label: "S", name: "Sunday" }
  ];
  const dayOrder = ["M0", "T1", "W2", "T3", "F4", "S5", "S6"];
  const sortedDays = dayOrder.filter(d => selectedDays.includes(d));

  function toMins(t) {
    const [h, m] = t.split(":").map(Number);
    return h * 60 + m;
  }

  const sortedHours = [...visibleHours].sort((a, b) => a - b);
  const firstHour = sortedHours[0];
  const lastHour = sortedHours[sortedHours.length - 1];

  const gridWidth = Math.round(device ? device.width / 3 : 400);
  const gridHeight = Math.round(device ? device.height / 3 : 700);
  const totalMinutes = (lastHour + 1 - firstHour) * 60;
  const pixelsPerMinute = (gridHeight - headerHeight) / totalMinutes;

  function timeToY(mins) {
    return headerHeight + (mins - firstHour * 60) * pixelsPerMinute;
  }

  function cellsForDay(day) {
    return classes
      .filter(c => c.days.includes(day))
      .sort((a, b) => toMins(a.startTime) - toMins(b.startTime));
  }

  function checkOverlap(newClass) {
    return classes.some(c =>
      c.days.some(d => newClass.days.includes(d)) &&
      !(newClass.endTime <= c.startTime || newClass.startTime >= c.endTime)
    );
  }

  function handleAddClass(newClass) {
    const withId = { ...newClass, id: Date.now() };
    if (checkOverlap(withId)) {
      alert("Overlapping class detected. Please adjust the time or days.");
      return;
    }
    setClasses([...classes, withId]);
    setShowModal(false);
  }

  function handleEditClass(updated) {
    const overlap = classes
      .filter(c => c.id !== updated.id)
      .some(c =>
        c.days.some(d => updated.days.includes(d)) &&
        !(updated.endTime <= c.startTime || updated.startTime >= c.endTime)
      );
    if (overlap) {
      alert("Overlapping class detected. Please adjust the time or days.");
      return false;
    }
    setClasses(classes.map(c => (c.id === updated.id ? updated : c)));
    return true;
  }

  function handleDeleteClass(id) {
    setClasses(classes.filter(c => c.id !== id));
  }

  function onImageSelect(e) {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = () => {
      setRawImage(reader.result);
      setShowCropper(true);
    };
    reader.readAsDataURL(file);
  }

  async function downloadTimetable(e) {
    if (e && e.preventDefault) e.preventDefault();
    if (!timetableRef.current || !device) return;
    const scale = device.width / gridWidth;
    try {
      const dataUrl = await toPng(timetableRef.current, { cacheBust: true, pixelRatio: scale });
      saveAs(dataUrl, "timetable.png");
    } catch (err) {
      console.error("Export failed:", err);
      alert("Export failed: " + (err.message || err));
    }
  }

  return (
    <div className="min-h-screen flex flex-col items-center bg-gray-100">
      <h1 className="text-2xl font-bold mt-6 mb-4">imey-scheduler</h1>
      <DevicePicker onSelect={setDevice} />
      {device && (
        <>
          <div className="flex space-x-4 mb-4">
            <button onClick={() => setShowModal(true)} className="px-4 py-2 bg-blue-500 text-white rounded">Add Class</button>
            <button onClick={() => setShowSettings(true)} className="px-4 py-2 bg-gray-700 text-white rounded">Settings</button>
            <button onClick={downloadTimetable} className="px-4 py-2 bg-green-500 text-white rounded">Download</button>
          </div>
          <DaysPicker selectedDays={selectedDays} onChange={setSelectedDays} />
          <div ref={timetableRef} style={{ width: gridWidth, height: gridHeight, position: "relative", background: "#fff" }}>
            {backgroundImage && <img src={backgroundImage} alt="bg" style={{ position: "absolute", width: "100%", height: "100%", objectFit: "cover" }} />}
            {sortedDays.map((day, i) => (
              <div key={day} style={{ position: "absolute", top: 0, left: (i * gridWidth) / sortedDays.length, width: gridWidth / sortedDays.length, height: headerHeight, lineHeight: `${headerHeight}px`, fontSize: activeSettings.dayTimeTextSize, color: activeSettings.dayTimeTextColor, textShadow: activeSettings.dayTimeTextShadow ? '0 1px 2px rgba(0,0,0,0.5)' : 'none', textAlign: 'center' }}>
                {dayLabels.find(dl => dl.name.startsWith(day[0])).label}
              </div>
            ))}
            {[...sortedHours].map((h, idx) => {
              const y = headerHeight + (h - firstHour) * 60 * pixelsPerMinute;
              return (
                <React.Fragment key={h}>
                  <div style={{ position: "absolute", top: y - 8, left: 0, width: 40, fontSize: activeSettings.dayTimeTextSize, color: activeSettings.dayTimeTextColor, textShadow: activeSettings.dayTimeTextShadow ? '0 1px 2px rgba(0,0,0,0.5)' : 'none' }}>{h}:00</div>
                  <div style={{ position: "absolute", top: y, left: 40, width: gridWidth - 40, height: 1, backgroundColor: '#eee' }} />
                </React.Fragment>
              );
            })}
            <div style={{ position: "absolute", top: headerHeight, left: 40, width: gridWidth - 40, height: gridHeight - headerHeight }}>
              {sortedDays.map((day, dayIdx) => {
                const colWidth = (gridWidth - 40) / sortedDays.length;
                return cellsForDay(day).map((c, idx) => {
                  const top = timeToY(toMins(c.startTime));
                  const bottom = timeToY(toMins(c.endTime));
                  return (
                    <div key={c.id} onClick={() => { setEditClass(c); setShowEdit(true); }} style={{ position: "absolute", top, left: 40 + dayIdx * colWidth, width: colWidth, height: bottom - top, backgroundColor: hexToRgba(c.color || '#4C9A2A', activeSettings.cellOpacity), fontSize: activeSettings.cellTextSize, color: activeSettings.cellTextColor, display: "flex", flexDirection: "column", justifyContent: "space-between", padding: 4, cursor: "pointer" }}>
                      <div style={{ fontWeight: activeSettings.timeBold ? 'bold' : 'normal' }}>{c.startTime}</div>
                      <div style={{ fontWeight: activeSettings.contentBold ? 'bold' : 'normal' }}>{c.name}</div>
                      {c.location && <div style={{ fontWeight: activeSettings.contentBold ? 'bold' : 'normal' }}>{c.location}</div>}
                      <div style={{ fontWeight: activeSettings.timeBold ? 'bold' : 'normal', textAlign: 'right' }}>{c.endTime}</div>
                    </div>
                  );
                });
              })}
            </div>
          </div>
          {showModal && <AddClassModal onSave={handleAddClass} onClose={() => setShowModal(false)} />}
          {showEdit && <EditClassModal classData={editClass} onSave={handleEditClass} onDelete={handleDeleteClass} onClose={() => setShowEdit(false)} />}
          {showSettings && <SettingsModal settings={settings} previewSettings={previewSettings} onChange={setPreviewSettings} onSave={() => { setSettings(previewSettings); setShowSettings(false); }} onClose={() => setShowSettings(false)} />}
          {showCropper && rawImage && <BackgroundCropperModal imageSrc={rawImage} width={device.width} height={device.height} onCropComplete={url => { setBackgroundImage(url); setShowCropper(false); }} onClose={() => setShowCropper(false)} />}
        </>
      )}
    </div>
  );
}

export default App;
