import React, { useRef, useState, useEffect } from "react";
import DevicePicker from "./DevicePicker";
import DaysPicker from "./DaysPicker";
import AddClassModal from "./AddClassModal";
import EditClassModal from "./EditClassModal";
import SettingsModal from "./SettingsModal";
import BackgroundCropperModal from "./BackgroundCropperModal";
import { toPng } from "html-to-image";
import { saveAs } from "file-saver";

// helper to convert hex color to rgba with transparency
function hexToRgba(hex, alpha = 0.5) {
  const [r, g, b] = hex.match(/\w\w/g).map(v => parseInt(v, 16));
  return `rgba(${r}, ${g}, ${b}, ${alpha})`;
}

const headerHeight = 40;
const initialHours = Array.from({ length: 13 }, (_, i) => 7 + i);

function App() {
  const [device, setDevice] = useState(null);
  const [visibleHours, setVisibleHours] = useState(initialHours);
  const [selectedDays, setSelectedDays] = useState(["M0", "T1", "W2", "T3", "F4"]);
  const [classes, setClasses] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [showEdit, setShowEdit] = useState(false);
  const [editClass, setEditClass] = useState(null);
  const [showCropper, setShowCropper] = useState(false);
  const [rawImage, setRawImage] = useState(null);
  const [backgroundImage, setBackgroundImage] = useState(null);
  const inputRef = useRef(null);
  const timetableRef = useRef(null);

  // global settings state
  const [settings, setSettings] = useState({
    cellOpacity: 0.5,
    cellTextSize: 11,
    cellTextColor: "#ffffff",
    cellTextShadow: true,
    cellTextBold: false,
    dayTimeTextSize: 13,
    dayTimeTextColor: "#ffffff",
  });
  const [previewSettings, setPreviewSettings] = useState(settings);
  const [showSettings, setShowSettings] = useState(false);

  // use preview while editing
  const activeSettings = showSettings ? previewSettings : settings;

  // day label mappings
  const dayLabels = [
    { label: "M", name: "Monday" },
    { label: "T", name: "Tuesday" },
    { label: "W", name: "Wednesday" },
    { label: "T", name: "Thursday" },
    { label: "F", name: "Friday" },
    { label: "S", name: "Saturday" },
    { label: "S", name: "Sunday" },
  ];
  const dayOrder = ["M0", "T1", "W2", "T3", "F4", "S5", "S6"];
  function sortDays(days) {
    return dayOrder.filter(d => days.includes(d));
  }

  // helpers for time in minutes
  function toMins(t) {
    const [h, m] = t.split(":").map(Number);
    return h * 60 + m;
  }

  // visible hours management
  function canDeleteHour(hour) {
    const start = hour * 60;
    const end = (hour + 1) * 60;
    return !classes.some(c =>
      c.days.some(d => selectedDays.includes(d)) &&
      toMins(c.startTime) < end && toMins(c.endTime) > start
    );
  }
  function addHour(hour) {
    setVisibleHours([...visibleHours, hour].sort((a, b) => a - b));
  }
  function removeHour(hour) {
    setVisibleHours(visibleHours.filter(h => h !== hour));
  }
  const missingHours = initialHours.filter(h => !visibleHours.includes(h));
  const sortedHours = [...visibleHours].sort((a, b) => a - b);

  // grid and safe-area
  const gridWidth = device ? device.width : 400;
  const gridHeight = device ? device.height : 700;
  const safeAreaOffset = (() => {
    if (!device) return 0;
    const dynamicIsland = [
      "iPhone 16 Pro Max","iPhone 14/15 Pro Max","iPhone 16 Pro","iPhone 14/15 Pro","iPhone 15/16 Plus","iPhone 15/16"
    ].includes(device.name);
    const notch = ["iPhone 14 Plus","iPhone 13/14","iPhone 12/13 Mini"].includes(device.name);
    if (dynamicIsland) return 59;
    if (notch) return 44;
    return 0;
  })();
  const totalRows = sortedHours.length;
  const gridDrawHeight = gridHeight - headerHeight;
  const firstHour = sortedHours[0];
  const lastHour = sortedHours[totalRows - 1];
  function timeToY(mins) {
    const minutesSinceStart = mins - firstHour * 60;
    const totalMinutes = (lastHour + 1 - firstHour) * 60;
    return headerHeight + (minutesSinceStart / totalMinutes) * gridDrawHeight;
  }

  // classes for day sorted
  function cellsForDay(day) {
    return classes
      .filter(c => c.days.includes(day))
      .sort((a, b) => toMins(a.startTime) - toMins(b.startTime));
  }

  // overlap detection
  function checkOverlap(newClass) {
    return classes.some(c =>
      c.days.some(d => newClass.days.includes(d)) &&
      !(newClass.endTime <= c.startTime || newClass.startTime >= c.endTime)
    );
  }

  // add, edit, delete handlers
  function handleAddClass(newClass) {
    const withId = { ...newClass, id: Date.now() };
    if (checkOverlap(withId)) {
      alert("Overlapping class detected. Please adjust the time or days.");
      return false;
    }
    setClasses([...classes, withId]);
    setShowModal(false);
    return true;
  }
  function handleEditClass(updated) {
    const overlap = classes
      .filter(c => c.id !== updated.id)
      .some(c =>
        c.days.some(d => updated.days.includes(d)) &&
        !(updated.endTime <= c.startTime || updated.startTime >= c.endTime)
      );
    if (overlap) {
      alert("Overlapping class detected. Please adjust the time or days.");
      return false;
    }
    setClasses(classes.map(c => (c.id === updated.id ? updated : c)));
    return true;
  }
  function handleDeleteClass(id) {
    setClasses(classes.filter(c => c.id !== id));
  }

  // image import and export
  function onImageSelect(e) {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = () => {
      setRawImage(reader.result);
      setShowCropper(true);
    };
    reader.readAsDataURL(file);
  }
  async function downloadTimetable(e) {
    e && e.preventDefault();
    if (!timetableRef.current || !device) return;
    const scale = device.width / timetableRef.current.clientWidth;
    try {
      const dataUrl = await toPng(timetableRef.current, { cacheBust: true, pixelRatio: scale });
      saveAs(dataUrl, "timetable.png");
    } catch (err) {
      console.error(err);
      alert("Export failed: " + err.message);
    }
  }

  return (
    <div className="min-h-screen flex flex-col items-center bg-gray-100">
      <h1 className="text-2xl font-bold mt-6 mb-4">imey-scheduler</h1>
      <DevicePicker onSelect={setDevice} />
      {device && (
        <div className="mt-8 flex flex-col items-center">
          {/* Controls */}
          <div className="flex gap-2 mb-4">
            <button
              className="px-4 py-2 bg-yellow-500 text-white rounded"
              onClick={() => { setPreviewSettings(settings); setShowSettings(true); }}
            >
              Settings
            </button>
            <button
              className="px-4 py-2 bg-green-600 text-white rounded"
              onClick={() => setShowModal(true)}
            >
              + Add Class
            </button>
            <button
              className="px-4 py-2 bg-blue-500 text-white rounded"
              onClick={() => inputRef.current && inputRef.current.click()}
            >
              Set Background Image
            </button>
            <button
              className="px-4 py-2 bg-purple-600 text-white rounded"
              onClick={downloadTimetable}
            >
              Download as Image
            </button>
          </div>

          {/* Settings Modal */}
          <SettingsModal
            visible={showSettings}
            settings={previewSettings}
            onChange={setPreviewSettings}
            onSave={() => { setSettings(previewSettings); setShowSettings(false); }}
            onClose={() => { setShowSettings(false); setPreviewSettings(settings); }}
          />

          {/* Add/Edit/Background Modals */}
          <AddClassModal
            visible={showModal}
            onClose={() => setShowModal(false)}
            onAdd={handleAddClass}
            days={selectedDays}
          />
          <EditClassModal
            visible={showEdit}
            initialData={editClass}
            days={selectedDays}
            onClose={() => setShowEdit(false)}
            onEditClass={cls => { const ok = handleEditClass(cls); if (ok) setShowEdit(false); return ok; }}
            onDeleteClass={id => { handleDeleteClass(id); setShowEdit(false); }}
          />
          {showCropper && rawImage && (
            <BackgroundCropperModal
              imageSrc={rawImage}
              width={device.width}
              height={device.height}
              onCropComplete={url => { setBackgroundImage(url); setShowCropper(false); }}
              onClose={() => setShowCropper(false)}
            />
          )}

          {/* Hidden file input */}
          <input
            type="file"
            accept="image/*"
            onChange={onImageSelect}
            ref={inputRef}
            style={{ display: "none" }}
          />

          {/* Timetable Preview */}
          <div
            ref={timetableRef}
            style={{
              width: gridWidth,
              height: gridHeight,
              background: "#fff",
              position: "relative",
              boxShadow: "0 2px 12px rgba(0,0,0,0.07)",
              overflow: "hidden",
            }}
          >
            {backgroundImage && (
              <img
                src={backgroundImage}
                alt=""
                style={{
                  position: "absolute",
                  left: 0,
                  top: 0,
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  zIndex: 0,
                }}
              />
            )}
            {/* Time Axis */}
            <div
              style={{
                position: "absolute",
                top: headerHeight + safeAreaOffset,
                left: 0,
                width: 40,
                height: gridDrawHeight,
                zIndex: 3,
                pointerEvents: "none",
              }}
            >
              {sortedHours.map(hour => (
                <div
                  key={hour}
                  style={{
                    position: "absolute",
                    top: timeToY(hour * 60) - headerHeight,
                    left: 0,
                    width: "100%",
                    textAlign: "center",
                    transform: "translateY(-50%)",
                    fontSize: activeSettings.dayTimeTextSize,
                    color: activeSettings.dayTimeTextColor,
                  }}
                >
                  {hour < 10 ? `0${hour}` : hour}
                </div>
              ))}
            </div>
            {/* Day Labels */}
            <div
              style={{
                position: "absolute",
                top: safeAreaOffset,
                left: 40,
                height: headerHeight,
                width: `calc(100% - 40px)`,
                display: "flex",
                zIndex: 3,
                pointerEvents: "none",
              }}
            >
              {selectedDays.map(day => (
                <div
                  key={day}
                  style={{
                    flex: 1,
                    textAlign: "center",
                    lineHeight: `${headerHeight}px`,
                    fontSize: activeSettings.dayTimeTextSize,
                    color: activeSettings.dayTimeTextColor,
                  }}
                >
                  {dayLabels[parseInt(day.slice(-1))].label}
                </div>
              ))}
            </div>
            {/* Class Cells */}
            <div
              style={{
                position: "absolute",
                top: headerHeight + safeAreaOffset,
                left: 40,
                width: `calc(100% - 40px)`,
                height: gridDrawHeight,
                zIndex: 4,
              }}
            >
              {selectedDays.map((day, dayIdx) => {
                const numDays = selectedDays.length;
                const colWidth = (gridWidth - 40) / numDays;
                const isLastCol = dayIdx === numDays - 1;
                return (
                  <React.Fragment key={day}>
                    {cellsForDay(day).map((c, idx) => {
                      const startY = timeToY(toMins(c.startTime)) - headerHeight;
                      const endY = timeToY(toMins(c.endTime)) - headerHeight;
                      let adjustTop = 0;
                      const sorted = cellsForDay(day);
                      if (idx > 0) {
                        const prev = sorted[idx - 1];
                        if (prev && toMins(prev.endTime) === toMins(c.startTime)) {
                          adjustTop = 1;
                        }
                      }
                      const realStartY = startY + adjustTop;
                      const realHeight = Math.max(12, endY - realStartY);
                      return (
                        <div
                          key={c.id}
                          onClick={() => { setEditClass(c); setShowEdit(true); }}
                          style={{
                            position: "absolute",
                            left: Math.round(colWidth * dayIdx),
                            width: isLastCol ? Math.ceil(colWidth) : Math.floor(colWidth),
                            top: realStartY,
                            height: realHeight,
                            background: hexToRgba(c.cellColor, activeSettings.cellOpacity),
                            boxSizing: "border-box",
                            borderRight: !isLastCol ? "1px solid transparent" : undefined,
                            color: activeSettings.cellTextColor,
                            fontSize: activeSettings.cellTextSize,
                            fontWeight: activeSettings.cellTextBold ? 'bold' : 'normal',
                            textShadow: activeSettings.cellTextShadow ? "0 0 2px #000" : 'none',
                            padding: 4,
                            cursor: "pointer",
                            display: "flex",
                            flexDirection: "column",
                            justifyContent: "space-between",
                            zIndex: 10,
                            overflow: "hidden",
                          }}
                        >
                          <div>{c.startTime}</div>
                          <div style={{ textAlign: 'center', overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'pre-line', wordBreak: 'break-word' }}>{c.className}</div>
                          {c.location && (
                            <div style={{ textAlign: 'center', overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'pre-line', wordBreak: 'break-word' }}>{c.location}</div>
                          )}
                          <div style={{ textAlign: 'right' }}>{c.endTime}</div>
                        </div>
                      );
                    })}
                  </React.Fragment>
                );
              })}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
