import React, { useRef, useState } from "react";
import DevicePicker from "./DevicePicker";
import DaysPicker from "./DaysPicker";
import AddClassModal from "./AddClassModal";
import BackgroundCropperModal from "./BackgroundCropperModal";
import { toPng } from "html-to-image";
import { saveAs } from "file-saver";

const headerHeight = 40;
const initialHours = Array.from({ length: 13 }, (_, i) => 7 + i);

function App() {
  const [device, setDevice] = useState(null);
  const [visibleHours, setVisibleHours] = useState(initialHours);
  const [selectedDays, setSelectedDays] = useState(["M0", "T1", "W2", "T3", "F4"]);
  const [showModal, setShowModal] = useState(false);
  const [classes, setClasses] = useState([]);
  const [showCropper, setShowCropper] = useState(false);
  const [rawImage, setRawImage] = useState(null);
  const [backgroundImage, setBackgroundImage] = useState(null);
  const inputRef = useRef(null);
  const timetableRef = useRef(null);

  const dayLabels = [
    { label: "M", name: "Monday" },
    { label: "T", name: "Tuesday" },
    { label: "W", name: "Wednesday" },
    { label: "T", name: "Thursday" },
    { label: "F", name: "Friday" },
    { label: "S", name: "Saturday" },
    { label: "S", name: "Sunday" }
  ];
  const dayOrder = ["M0", "T1", "W2", "T3", "F4", "S5", "S6"];
  function sortDays(days) {
    return dayOrder.filter(d => days.includes(d));
  }

  function toMins(t) {
    let [h, m] = t.split(":").map(Number);
    return h * 60 + m;
  }

  function canDeleteHour(hour) {
    const hourStart = hour * 60;
    const hourEnd = (hour + 1) * 60;
    return !classes.some(c => {
      const cStart = toMins(c.startTime);
      const cEnd = toMins(c.endTime);
      return c.days.some(d => selectedDays.includes(d)) &&
        cStart < hourEnd && cEnd > hourStart;
    });
  }
  function addHour(hour) {
    const newHours = [...visibleHours, hour].sort((a, b) => a - b);
    setVisibleHours(newHours);
  }
  function removeHour(hour) {
    setVisibleHours(visibleHours.filter(h => h !== hour));
  }

  const missingHours = initialHours.filter(h => !visibleHours.includes(h));
  const sortedHours = [...visibleHours].sort((a, b) => a - b);

  const gridWidth = Math.round(device ? device.width / 3 : 400);
  const gridHeight = Math.round(device ? device.height / 3 : 700);
  const totalRows = sortedHours.length;
  const gridDrawHeight = gridHeight - headerHeight;
  const firstHour = sortedHours[0];
  const lastHour = sortedHours[totalRows - 1];

  function timeToY(mins) {
    const minutesSinceStart = mins - firstHour * 60;
    const totalMinutes = (lastHour + 1 - firstHour) * 60;
    return headerHeight + (minutesSinceStart / totalMinutes) * gridDrawHeight;
  }
  function cellsForDay(day) {
    return classes
      .filter(c => c.days.includes(day))
      .sort((a, b) => toMins(a.startTime) - toMins(b.startTime));
  }
  function checkOverlap(newClass) {
    return classes.some(c =>
      c.days.some(d => newClass.days.includes(d)) &&
      !(
        newClass.endTime <= c.startTime ||
        newClass.startTime >= c.endTime
      )
    );
  }
  function handleAddClass(newClass) {
    if (checkOverlap(newClass)) {
      alert("Overlapping class detected. Please adjust the time or days.");
      return;
    }
    setClasses([...classes, newClass]);
    setShowModal(false);
  }
  function onImageSelect(e) {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = () => {
      setRawImage(reader.result);
      setShowCropper(true);
    };
    reader.readAsDataURL(file);
  }

  // DOM-to-image download with proper error handling
  async function downloadTimetable(e) {
    if (e && e.preventDefault) e.preventDefault();
    if (!timetableRef.current || !device) return;

    // Compute scale factor between full-res and on-screen grid
    const scale = device.width / gridWidth;  // e.g. 1080/360 = 3

    try {
      const dataUrl = await toPng(timetableRef.current, {
        cacheBust: true,
        pixelRatio: scale
      });
      saveAs(dataUrl, "timetable.png");
    } catch (err) {
      console.error("Export failed:", err);
      alert("Export failed: " + (err.message || err));
    }
  }

  return (
    <div className="min-h-screen flex flex-col items-center bg-gray-100">
      <h1 className="text-2xl font-bold mt-6 mb-4">imey-scheduler</h1>
      <DevicePicker onSelect={setDevice} />
      {device && (
        <div className="mt-8 flex flex-col items-center">
          {/* Set Background Button */}
          <button
            className="mb-2 px-4 py-2 bg-blue-500 text-white rounded"
            onClick={() => inputRef.current && inputRef.current.click()}
          >
            Set Background Image
          </button>
          <input
            type="file"
            accept="image/*"
            onChange={onImageSelect}
            style={{ display: "none" }}
            ref={inputRef}
          />

          <DaysPicker
            selectedDays={selectedDays}
            setSelectedDays={days => setSelectedDays(sortDays(days))}
          />

          <button
            className="mb-2 px-4 py-2 bg-green-600 text-white rounded"
            onClick={() => setShowModal(true)}
          >
            + Add Class
          </button>

          {/* Add/Remove hour row controls */}
          <div className="mb-2 flex flex-wrap items-center gap-2">
            <span className="mr-2 text-sm font-medium">Manage time rows:</span>
            {sortedHours.map(hour => (
              <button
                key={hour}
                onClick={() => canDeleteHour(hour) && removeHour(hour)}
                className={`text-xs px-2 py-1 rounded border ${canDeleteHour(hour) ? "bg-red-100 hover:bg-red-200 text-red-600" : "bg-gray-100 text-gray-400 cursor-not-allowed"}`}
                disabled={!canDeleteHour(hour)}
                title={canDeleteHour(hour) ? "Delete this hour row" : "Cannot delete (row contains class)"}
                style={{ minWidth: 40 }}
              >
                {hour < 10 ? "0" + hour : hour}:00 ✕
              </button>
            ))}
            {missingHours.length > 0 && (
              <select
                className="ml-4 text-xs border p-1 rounded"
                onChange={e => {
                  const val = Number(e.target.value);
                  if (val) addHour(val);
                  e.target.value = "";
                }}
                defaultValue=""
              >
                <option value="" disabled>
                  + Add row...
                </option>
                {missingHours.map(h => (
                  <option key={h} value={h}>
                    {h < 10 ? "0" + h : h}:00
                  </option>
                ))}
              </select>
            )}
          </div>

          {/* Overlay Timetable Grid */}
          <div
            ref={timetableRef}
            style={{
              width: `${gridWidth}px`,
              height: `${gridHeight}px`,
              /* removed the grey border */
              /* removed the rounded corners */
              background: "#fff",
              position: "relative",
              boxShadow: "0 2px 12px rgba(0,0,0,0.07)",
              overflow: "hidden",
            }}
          >
            {backgroundImage && (
              <img
                src={backgroundImage}
                alt=""
                style={{
                  position: "absolute",
                  left: 0,
                  top: 0,
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  zIndex: 0,
                  pointerEvents: "none",
                  userSelect: "none",
                }}
              />
            )}
            {/* Time axis (overlay) */}
            <div
              style={{
                position: "absolute",
                left: 0,
                top: headerHeight,
                width: 40,
                height: gridHeight - headerHeight,
                zIndex: 3,
                pointerEvents: "none",
                background: "transparent"
              }}
            >
              {sortedHours.map(hour => (
                <div
                  key={hour}
                  style={{
                    position: "absolute",
                    top: timeToY(hour * 60) - headerHeight,
                    left: 0,
                    width: "100%",
                    textAlign: "center",
                    transform: "translateY(-50%)",
                    fontSize: 13,
                    color: "#fff",
                    textShadow: "0 0 4px #000,0 0 2px #000"
                  }}
                >
                  {hour < 10 ? "0" + hour : hour}
                </div>
              ))}
            </div>
            {/* Day labels row (overlay) */}
            <div
              style={{
                position: "absolute",
                left: 40,
                top: 0,
                height: headerHeight,
                width: `calc(100% - 40px)`,
                display: "flex",
                zIndex: 3,
                background: "transparent"
              }}
            >
              {selectedDays.map(day => (
                <div
                  key={day}
                  style={{
                    flex: 1,
                    fontWeight: "normal",
                    fontSize: 13,
                    color: "#fff",
                    textShadow: "0 0 4px #000,0 0 2px #000",
                    textAlign: "center",
                    lineHeight: `${headerHeight}px`
                  }}
                >
                  {dayLabels[parseInt(day.slice(-1))].label}
                </div>
              ))}
            </div>
            {/* Class cells (overlay) */}
            {/* Class cells */}
            <div
              style={{
                position: "absolute",
                left: 40,
                top: headerHeight,
                width: `calc(100% - 40px)`,
                height: gridHeight - headerHeight,
                zIndex: 4
              }}
            >
              {selectedDays.map((day, colIdx) => {
                const cols = selectedDays.length;
                const widthPct = 100 / cols;
                const leftPct  = widthPct * colIdx;

                return cellsForDay(day).map((c, idx) => {
                  // compute Y in px as before
                  const y0 = timeToY(toMins(c.startTime)) - headerHeight;
                  const y1 = timeToY(toMins(c.endTime))   - headerHeight;
                  // collapse touching cells by 1px
                  let offset = 0;
                  const list = cellsForDay(day);
                  if (idx > 0 && toMins(list[idx - 1].endTime) === toMins(c.startTime)) {
                    offset = 1;
                  }
                  const topPx    = y0 + offset;
                  const heightPx = Math.max(12, y1 - topPx);

                  return (
                    <div
                      key={`${day}-${idx}`}
                      style={{
                        position: "absolute",
                        left:   `${leftPct}%`,
                        width:  `${widthPct}%`,
                        top:    topPx,
                        height: heightPx,
                        background: c.cellColor,
                        opacity:    0.96,
                        borderRadius: 0,
                        color:      c.textColor,
                        fontSize:   c.textSize,
                        fontWeight: 600,
                        padding:    "0 4px",
                        display:    "flex",
                        flexDirection: "column",
                        justifyContent:"space-between",
                        overflow:   "hidden",
                        boxShadow:  "none"
                      }}
                    >
                      <div style={{ fontSize:11, fontWeight:500, textShadow:"0 0 2px #000" }}>
                        {c.startTime}
                      </div>
                      <div style={{
                        flex:1,
                        display:"flex",
                        flexDirection:"column",
                        justifyContent:"center",
                        textAlign:"center",
                        overflow:"hidden",
                        whiteSpace:"pre-line",
                        wordBreak:"break-word",
                        textShadow:"0 0 2px #000"
                      }}>
                        <div style={{
                          fontWeight:700,
                          overflow:"hidden",
                          textOverflow:"ellipsis"
                        }}>{c.className}</div>
                        {c.location && (
                          <div style={{
                            fontSize:c.textSize,
                            overflow:"hidden",
                            textOverflow:"ellipsis"
                          }}>{c.location}</div>
                        )}
                      </div>
                      <div style={{ fontSize:11, textAlign:"right", textShadow:"0 0 2px #000" }}>
                        {c.endTime}
                      </div>
                    </div>
                  );
                });
              })}
            </div>
          </div>
          <button
            className="mt-2 px-4 py-2 bg-purple-600 text-white rounded"
            onClick={downloadTimetable}
          >
            Download as Image
          </button>
          <div className="mt-2 text-xs text-gray-400">
            (Preview is scaled down for your screen. Final export will be full resolution.)
          </div>
        </div>
      )}

      {/* Add Class Modal */}
      <AddClassModal
        visible={showModal}
        onClose={() => setShowModal(false)}
        onAdd={handleAddClass}
        days={selectedDays}
      />
      {/* Background Cropper Modal */}
      {showCropper && rawImage && device && (
        <BackgroundCropperModal
          imageSrc={rawImage}
          width={device.width}
          height={device.height}
          onCropComplete={croppedUrl => {
            setBackgroundImage(croppedUrl);
            setShowCropper(false);
          }}
          onClose={() => setShowCropper(false)}
        />
      )}
    </div>
  );
}

export default App;
