import React, { useRef, useState } from "react";
import DevicePicker from "./DevicePicker";
import DaysPicker from "./DaysPicker";
import AddClassModal from "./AddClassModal";
import BackgroundCropperModal from "./BackgroundCropperModal";
import { toPng } from "html-to-image";
import { saveAs } from "file-saver";

const headerHeight = 40;
const initialHours = Array.from({ length: 13 }, (_, i) => 7 + i);

function App() {
  const [device, setDevice] = useState(null);
  const [visibleHours, setVisibleHours] = useState(initialHours);
  const [selectedDays, setSelectedDays] = useState(["M0", "T1", "W2", "T3", "F4"]);
  const [showModal, setShowModal] = useState(false);
  const [classes, setClasses] = useState([]);
  const [showCropper, setShowCropper] = useState(false);
  const [rawImage, setRawImage] = useState(null);
  const [backgroundImage, setBackgroundImage] = useState(null);
  const inputRef = useRef(null);
  const timetableRef = useRef(null);

  const dayLabels = [
    { label: "M", name: "Monday" },
    { label: "T", name: "Tuesday" },
    { label: "W", name: "Wednesday" },
    { label: "T", name: "Thursday" },
    { label: "F", name: "Friday" },
    { label: "S", name: "Saturday" },
    { label: "S", name: "Sunday" }
  ];
  const dayOrder = ["M0", "T1", "W2", "T3", "F4", "S5", "S6"];
  function sortDays(days) {
    return dayOrder.filter(d => days.includes(d));
  }

  function toMins(t) {
    let [h, m] = t.split(":").map(Number);
    return h * 60 + m;
  }

  function canDeleteHour(hour) {
    const start = hour * 60, end = (hour + 1) * 60;
    return !classes.some(c => {
      const cs = toMins(c.startTime), ce = toMins(c.endTime);
      return c.days.some(d => selectedDays.includes(d)) && cs < end && ce > start;
    });
  }
  function addHour(hour) {
    setVisibleHours(v => [...v, hour].sort((a, b) => a - b));
  }
  function removeHour(hour) {
    setVisibleHours(v => v.filter(h => h !== hour));
  }

  const missingHours = initialHours.filter(h => !visibleHours.includes(h));
  const sortedHours = [...visibleHours].sort((a, b) => a - b);

  // Preview grid size
  const gridWidth = Math.round(device ? device.width / 3 : 400);
  const gridHeight = Math.round(device ? device.height / 3 : 700);
  // --- notch/dynamic-island safe area ---
  const safeAreaPx = device
    ? device.name.includes("Pro")
      ? 59   // Dynamic Island models
      : 44   // Notch models
    : 0;
  const safeAreaPreview = safeAreaPx / 3;

  const totalRows = sortedHours.length;
  const gridDrawHeight = gridHeight - headerHeight;
  const firstHour = sortedHours[0];
  const lastHour = sortedHours[totalRows - 1];

  function timeToY(mins) {
    const since = mins - firstHour * 60;
    const total = (lastHour + 1 - firstHour) * 60;
    return headerHeight + (since / total) * gridDrawHeight;
  }
  function cellsForDay(day) {
    return classes
      .filter(c => c.days.includes(day))
      .sort((a, b) => toMins(a.startTime) - toMins(b.startTime));
  }
  function checkOverlap(newClass) {
    return classes.some(c =>
      c.days.some(d => newClass.days.includes(d)) &&
      !(newClass.endTime <= c.startTime || newClass.startTime >= c.endTime)
    );
  }
  function handleAddClass(nc) {
    if (checkOverlap(nc)) {
      alert("Overlapping class detected. Please adjust the time or days.");
      return;
    }
    setClasses(cs => [...cs, nc]);
    setShowModal(false);
  }
  function onImageSelect(e) {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onload = () => {
      setRawImage(reader.result);
      setShowCropper(true);
    };
    reader.readAsDataURL(file);
  }

  async function downloadTimetable(e) {
    if (e?.preventDefault) e.preventDefault();
    if (!timetableRef.current || !device) return;
    const scale = device.width / gridWidth;  // e.g. 1080/360 = 3
    try {
      const dataUrl = await toPng(timetableRef.current, { cacheBust: true, pixelRatio: scale });
      saveAs(dataUrl, "timetable.png");
    } catch (err) {
      console.error("Export failed:", err);
      alert("Export failed: " + (err.message || err));
    }
  }

  return (
    <div className="min-h-screen flex flex-col items-center bg-gray-100">
      <h1 className="text-2xl font-bold mt-6 mb-4">imey-scheduler</h1>
      <DevicePicker onSelect={setDevice} />

      {device && (
        <div className="mt-8 flex flex-col items-center">

          {/* Background */}
          <button
            className="mb-2 px-4 py-2 bg-blue-500 text-white rounded"
            onClick={() => inputRef.current?.click()}
          >
            Set Background Image
          </button>
          <input
            type="file"
            accept="image/*"
            onChange={onImageSelect}
            ref={inputRef}
            style={{ display: "none" }}
          />

          {/* Days */}
          <DaysPicker
            selectedDays={selectedDays}
            setSelectedDays={ds => setSelectedDays(sortDays(ds))}
          />

          {/* Add Class */}
          <button
            className="mb-2 px-4 py-2 bg-green-600 text-white rounded"
            onClick={() => setShowModal(true)}
          >
            + Add Class
          </button>

          {/* Time‐row controls */}
          <div className="mb-2 flex flex-wrap items-center gap-2">
            <span className="text-sm font-medium mr-2">Manage time rows:</span>
            {sortedHours.map(h => (
              <button
                key={h}
                disabled={!canDeleteHour(h)}
                onClick={() => canDeleteHour(h) && removeHour(h)}
                className={`text-xs px-2 py-1 rounded border ${
                  canDeleteHour(h)
                    ? "bg-red-100 hover:bg-red-200 text-red-600"
                    : "bg-gray-100 text-gray-400 cursor-not-allowed"
                }`}
                style={{ minWidth: 40 }}
                title={
                  canDeleteHour(h)
                    ? "Delete this hour row"
                    : "Cannot delete (row contains class)"
                }
              >
                {h < 10 ? "0" + h : h}:00 ✕
              </button>
            ))}
            {missingHours.length > 0 && (
              <select
                className="ml-4 text-xs border p-1 rounded"
                defaultValue=""
                onChange={e => {
                  addHour(Number(e.target.value));
                  e.target.value = "";
                }}
              >
                <option value="" disabled>
                  + Add row...
                </option>
                {missingHours.map(h => (
                  <option key={h} value={h}>
                    {h < 10 ? "0" + h : h}:00
                  </option>
                ))}
              </select>
            )}
          </div>

          {/* Timetable */}
          <div
            ref={timetableRef}
            style={{
              marginTop: safeAreaPreview,
              width: `${gridWidth}px`,
              height: `${gridHeight}px`,
              border: "2px solid #aaa",
              borderRadius: "20px",
              background: "#fff",
              position: "relative",
              boxShadow: "0 2px 12px rgba(0,0,0,0.07)",
              overflow: "hidden"
            }}
          >
            {backgroundImage && (
              <img
                src={backgroundImage}
                alt=""
                style={{
                  position: "absolute",
                  top: 0,
                  left: 0,
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  zIndex: 0,
                  pointerEvents: "none"
                }}
              />
            )}

            {/* Time axis */}
            <div
              style={{
                position: "absolute",
                left: 0,
                top: headerHeight,
                width: 40,
                height: gridHeight - headerHeight,
                pointerEvents: "none",
                zIndex: 3
              }}
            >
              {sortedHours.map(h => (
                <div
                  key={h}
                  style={{
                    position: "absolute",
                    top: timeToY(h * 60) - headerHeight,
                    width: "100%",
                    textAlign: "center",
                    transform: "translateY(-50%)",
                    fontSize: 13,
                    color: "#fff",
                    textShadow: "0 0 4px #000, 0 0 2px #000"
                  }}
                >
                  {h < 10 ? "0" + h : h}
                </div>
              ))}
            </div>

            {/* Day labels */}
            <div
              style={{
                position: "absolute",
                left: 40,
                top: 0,
                height: headerHeight,
                width: `calc(100% - 40px)`,
                display: "flex",
                zIndex: 3
              }}
            >
              {selectedDays.map(day => (
                <div
                  key={day}
                  style={{
                    flex: 1,
                    fontSize: 13,
                    fontWeight: "normal",
                    color: "#fff",
                    textShadow: "0 0 4px #000, 0 0 2px #000",
                    textAlign: "center",
                    lineHeight: `${headerHeight}px`
                  }}
                >
                  {dayLabels[parseInt(day.slice(-1))].label}
                </div>
              ))}
            </div>

            {/* Class cells */}
            <div
              style={{
                position: "absolute",
                left: 40,
                top: headerHeight,
                width: `calc(100% - 40px)`,
                height: gridHeight - headerHeight,
                zIndex: 4
              }}
            >
              {selectedDays.map((day, idxDay) => {
                const cols = selectedDays.length;
                const colW = (gridWidth - 40) / cols;
                return (
                  <React.Fragment key={day}>
                    {cellsForDay(day).map((c, idx) => {
                      const startY = timeToY(toMins(c.startTime)) - headerHeight;
                      const endY = timeToY(toMins(c.endTime)) - headerHeight;
                      let offset = 0;
                      const list = cellsForDay(day);
                      if (idx > 0 && toMins(list[idx - 1].endTime) === toMins(c.startTime)) {
                        offset = 1;
                      }
                      const top = startY + offset;
                      const h = Math.max(12, endY - top);
                      const isLast = idxDay === cols - 1;

                      return (
                        <div
                          key={idx}
                          style={{
                            position: "absolute",
                            left: Math.round(colW * idxDay),
                            width: isLast ? Math.ceil(colW) : Math.floor(colW),
                            top,
                            height: h,
                            background: c.cellColor,
                            opacity: 0.96,
                            borderRadius: 0,
                            color: c.textColor,
                            fontSize: c.textSize,
                            fontWeight: 600,
                            boxShadow: "none",
                            padding: 0,
                            display: "flex",
                            flexDirection: "column",
                            justifyContent: "space-between",
                            cursor: "pointer",
                            overflow: "hidden"
                          }}
                        >
                          <div style={{ fontSize: 11, fontWeight: 500, textShadow: "0 0 2px #000" }}>
                            {c.startTime}
                          </div>
                          <div
                            style={{
                              flex: 1,
                              textAlign: "center",
                              padding: 2,
                              display: "flex",
                              flexDirection: "column",
                              justifyContent: "center",
                              overflow: "hidden",
                              whiteSpace: "pre-line",
                              wordBreak: "break-word"
                            }}
                          >
                            <div style={{
                              fontWeight: 700,
                              textShadow: "0 0 2px #000",
                              overflow: "hidden",
                              textOverflow: "ellipsis"
                            }}>
                              {c.className}
                            </div>
                            {c.location && (
                              <div style={{
                                fontSize: c.textSize,
                                textShadow: "0 0 2px #000",
                                overflow: "hidden",
                                textOverflow: "ellipsis"
                              }}>
                                {c.location}
                              </div>
                            )}
                          </div>
                          <div style={{ fontSize: 11, textAlign: "right", textShadow: "0 0 2px #000" }}>
                            {c.endTime}
                          </div>
                        </div>
                      );
                    })}
                  </React.Fragment>
                );
              })}
            </div>
          </div>

          {/* Download */}
          <button
            className="mt-2 px-4 py-2 bg-purple-600 text-white rounded"
            onClick={downloadTimetable}
          >
            Download as Image
          </button>
          <div className="mt-2 text-xs text-gray-400">
            (Preview is scaled down for your screen. Final export will be full resolution.)
          </div>
        </div>
      )}

      <AddClassModal
        visible={showModal}
        onClose={() => setShowModal(false)}
        onAdd={handleAddClass}
        days={selectedDays}
      />

      {showCropper && rawImage && device && (
        <BackgroundCropperModal
          imageSrc={rawImage}
          width={device.width}
          height={device.height}
          onCropComplete={croppedUrl => {
            setBackgroundImage(croppedUrl);
            setShowCropper(false);
          }}
          onClose={() => setShowCropper(false)}
        />
      )}
    </div>
  );
}

export default App;
