import React, { useState } from "react";
import DevicePicker from "./DevicePicker";

function App() {
  const [device, setDevice] = useState(null);

  return (
    <div className="min-h-screen flex flex-col items-center bg-gray-100">
      <h1 className="text-2xl font-bold mt-6 mb-4">Timetable Wallpaper Creator</h1>
      <DevicePicker onSelect={setDevice} />
      {device && (
        <div className="mt-8 flex flex-col items-center">
          <div
            style={{
              width: `${Math.round(device.width / 3)}px`, // scaled down for screen display
              height: `${Math.round(device.height / 3)}px`, // scale for demo, real export will use real px
              border: "2px solid #aaa",
              borderRadius: "20px",
              background: "#fff",
              position: "relative",
              boxShadow: "0 2px 12px rgba(0,0,0,0.07)",
              overflow: "hidden"
            }}
          >
            {/* This blank area is where the timetable and background will appear */}
            <div
              style={{
                position: "absolute",
                top: 8,
                left: 8,
                color: "#bbb",
                fontSize: 16
              }}
            >
              {device.name} ({device.width}Ã—{device.height})
            </div>
          </div>
          <div className="mt-2 text-xs text-gray-400">
            (Preview is scaled down for your screen. Final export will be full resolution.)
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
