import React, { useRef, useState } from "react";
import DevicePicker from "./DevicePicker";
import DaysPicker from "./DaysPicker";
import AddClassModal from "./AddClassModal";
import BackgroundCropperModal from "./BackgroundCropperModal";
import { toPng } from "html-to-image";
import { saveAs } from "file-saver";

const headerHeight = 40;
const initialHours = Array.from({ length: 13 }, (_, i) => 7 + i);

export default function App() {
  const [device, setDevice] = useState(null);
  const [visibleHours, setVisibleHours] = useState(initialHours);
  const [selectedDays, setSelectedDays] = useState(["M0","T1","W2","T3","F4"]);
  const [classes, setClasses] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [rawImage, setRawImage] = useState(null);
  const [backgroundImage, setBackgroundImage] = useState(null);
  const [showCropper, setShowCropper] = useState(false);
  const inputRef = useRef(null);
  const timetableRef = useRef(null);

  const dayLabels = [
    { label: "M", name: "Monday" },
    { label: "T", name: "Tuesday" },
    { label: "W", name: "Wednesday" },
    { label: "T", name: "Thursday" },
    { label: "F", name: "Friday" },
    { label: "S", name: "Saturday" },
    { label: "S", name: "Sunday" },
  ];
  const dayOrder = ["M0","T1","W2","T3","F4","S5","S6"];
  function sortDays(days) { return dayOrder.filter(d=>days.includes(d)); }

  function toMins(t){ const [h,m]=t.split(":").map(Number); return h*60+m; }
  function canDeleteHour(h){
    const start=h*60, end=(h+1)*60;
    return !classes.some(c=>{
      const cs=toMins(c.startTime), ce=toMins(c.endTime);
      return c.days.some(d=>selectedDays.includes(d)) && cs<end && ce>start;
    });
  }
  function addHour(h){ setVisibleHours(v=>[...v,h].sort((a,b)=>a-b)); }
  function removeHour(h){ setVisibleHours(v=>v.filter(x=>x!==h)); }

  const missingHours = initialHours.filter(h=>!visibleHours.includes(h));
  const sortedHours  = [...visibleHours].sort((a,b)=>a-b);

  const gridWidth  = Math.round(device ? device.width/3  : 400);
  const gridHeight = Math.round(device ? device.height/3 : 700);

  // SAFE AREA: real pixels ≈59px on Dynamic-Island, ≈44px on notch
  const safeAreaPx      = device
    ? device.name.includes("Pro") ? 59 : 44
    : 0;
  const safeAreaPreview = safeAreaPx / 3;

  const totalRows     = sortedHours.length;
  const gridDrawH     = gridHeight - headerHeight;
  const firstHour     = sortedHours[0];
  const lastHour      = sortedHours[totalRows-1];

  function timeToY(mins){
    const since = mins - firstHour*60;
    const total = (lastHour+1-firstHour)*60;
    return headerHeight + (since/total)*gridDrawH;
  }

  function cellsForDay(day){
    return classes
      .filter(c=>c.days.includes(day))
      .sort((a,b)=>toMins(a.startTime)-toMins(b.startTime));
  }

  function checkOverlap(nc){
    return classes.some(c=>
      c.days.some(d=>nc.days.includes(d)) &&
      !(nc.endTime<=c.startTime||nc.startTime>=c.endTime)
    );
  }

  function handleAddClass(nc){
    if(checkOverlap(nc)){
      alert("Overlapping class detected.");
      return;
    }
    setClasses(cs=>[...cs,nc]);
    setShowModal(false);
  }

  function onImageSelect(e){
    const file=e.target.files[0];
    if(!file) return;
    const reader=new FileReader();
    reader.onload=()=>{ setRawImage(reader.result); setShowCropper(true); };
    reader.readAsDataURL(file);
  }

  async function downloadTimetable(e){
    e?.preventDefault();
    if(!timetableRef.current||!device) return;
    const scale = device.width / gridWidth;
    try{
      const dataUrl = await toPng(timetableRef.current,{
        cacheBust:true, pixelRatio:scale
      });
      saveAs(dataUrl,"timetable.png");
    }catch(err){
      console.error("Export failed:", err);
      alert("Export failed: "+(err.message||err));
    }
  }

  return (
    <div className="min-h-screen flex flex-col items-center bg-gray-100">
      <h1 className="text-2xl font-bold mt-6 mb-4">imey-scheduler</h1>
      <DevicePicker onSelect={setDevice}/>

      {device && (
      <div className="mt-8 flex flex-col items-center">

        {/* Background picker */}
        <button
          className="mb-2 px-4 py-2 bg-blue-500 text-white rounded"
          onClick={()=>inputRef.current?.click()}
        >Set Background Image</button>
        <input
          ref={inputRef}
          type="file"
          accept="image/*"
          onChange={onImageSelect}
          style={{display:"none"}}
        />

        {/* Days picker */}
        <DaysPicker
          selectedDays={selectedDays}
          setSelectedDays={ds=>setSelectedDays(sortDays(ds))}
        />

        {/* Add class */}
        <button
          className="mb-2 px-4 py-2 bg-green-600 text-white rounded"
          onClick={()=>setShowModal(true)}
        >+ Add Class</button>

        {/* Hour row controls */}
        <div className="mb-2 flex flex-wrap items-center gap-2">
          <span className="mr-2 text-sm font-medium">Manage time rows:</span>
          {sortedHours.map(h=>(
            <button
              key={h}
              disabled={!canDeleteHour(h)}
              onClick={()=>canDeleteHour(h)&&removeHour(h)}
              className={`text-xs px-2 py-1 rounded border ${
                canDeleteHour(h)
                  ? "bg-red-100 hover:bg-red-200 text-red-600"
                  : "bg-gray-100 text-gray-400 cursor-not-allowed"
              }`}
              style={{minWidth:40}}
            >{h<10?`0${h}`:h}:00 ✕</button>
          ))}
          {missingHours.length>0 && (
            <select
              defaultValue=""
              className="ml-4 text-xs border p-1 rounded"
              onChange={e=>{
                addHour(Number(e.target.value));
                e.target.value="";
              }}
            >
              <option disabled value="">+ Add row...</option>
              {missingHours.map(h=>(
                <option key={h} value={h}>
                  {h<10?`0${h}`:h}:00
                </option>
              ))}
            </select>
          )}
        </div>

        {/* Wallpaper + overlay */}
        <div
          style={{
            position:"relative",
            width:`${gridWidth}px`,
            height:`${gridHeight}px`,
            border:"2px solid #aaa",
            borderRadius:"20px",
            boxShadow:"0 2px 12px rgba(0,0,0,0.07)",
            overflow:"hidden",
            background:"#fff"
          }}
        >
          {/* Full-height wallpaper */}
          {backgroundImage && (
            <img
              src={backgroundImage}
              alt=""
              style={{
                position:"absolute",
                top:0,left:0,
                width:"100%",height:"100%",
                objectFit:"cover",zIndex:0,
                pointerEvents:"none"
              }}
            />
          )}

          {/* Timetable overlay */}
          <div
            ref={timetableRef}
            style={{
              position:"absolute",
              top:safeAreaPreview,
              left:0,
              width:"100%",
              height:`calc(100% - ${safeAreaPreview}px)`,
              zIndex:1
            }}
          >
            {/* Time axis */}
            <div
              style={{
                position:"absolute",
                left:0, top:headerHeight,
                width:40,
                height:gridHeight-headerHeight,
                pointerEvents:"none",
                zIndex:3
              }}
            >
              {sortedHours.map(h=>(
                <div
                  key={h}
                  style={{
                    position:"absolute",
                    top:timeToY(h*60)-headerHeight,
                    width:"100%",
                    textAlign:"center",
                    transform:"translateY(-50%)",
                    fontSize:13,
                    color:"#fff",
                    textShadow:"0 0 4px #000,0 0 2px #000"
                  }}
                >{h<10?`0${h}`:h}</div>
              ))}
            </div>

            {/* Day labels */}
            <div
              style={{
                position:"absolute",
                left:40, top:0,
                width:`calc(100% - 40px)`,
                height:headerHeight,
                display:"flex",
                zIndex:3
              }}
            >
              {selectedDays.map(day=>(
                <div
                  key={day}
                  style={{
                    flex:1,
                    fontSize:13,
                    fontWeight:"normal",
                    color:"#fff",
                    textShadow:"0 0 4px #000,0 0 2px #000",
                    textAlign:"center",
                    lineHeight:`${headerHeight}px`
                  }}
                >{dayLabels[parseInt(day.slice(-1))].label}</div>
              ))}
            </div>

            {/* Class cells */}
            <div
              style={{
                position:"absolute",
                left:40, top:headerHeight,
                width:`calc(100% - 40px)`,
                height:gridHeight-headerHeight,
                zIndex:4
              }}
            >
              {selectedDays.map((day, iDay)=>{
                const cols = selectedDays.length;
                const colW = (gridWidth-40)/cols;
                return (
                  <React.Fragment key={day}>
                    {cellsForDay(day).map((c,i)=>{
                      const y0 = timeToY(toMins(c.startTime))-headerHeight;
                      const y1 = timeToY(toMins(c.endTime))-headerHeight;
                      // collapse touching cells
                      let off=0;
                      const arr=cellsForDay(day);
                      if(i>0 && toMins(arr[i-1].endTime)===toMins(c.startTime)){
                        off=1;
                      }
                      const top=y0+off, h=Math.max(12,y1-top);
                      const isLast=iDay===cols-1;
                      return (
                        <div
                          key={i}
                          style={{
                            position:"absolute",
                            left:Math.round(colW*iDay),
                            width:isLast?Math.ceil(colW):Math.floor(colW),
                            top, height:h,
                            background:c.cellColor,
                            opacity:0.96,
                            borderRadius:0,
                            color:c.textColor,
                            fontSize:c.textSize,
                            fontWeight:600,
                            boxShadow:"none",
                            display:"flex",
                            flexDirection:"column",
                            justifyContent:"space-between",
                            overflow:"hidden",
                            padding:"0 4px"
                          }}
                        >
                          <div style={{fontSize:11,fontWeight:500,textShadow:"0 0 2px #000"}}>
                            {c.startTime}
                          </div>
                          <div style={{
                            flex:1,
                            textAlign:"center",
                            overflow:"hidden",
                            whiteSpace:"pre-line",
                            wordBreak:"break-word",
                            display:"flex",
                            flexDirection:"column",
                            justifyContent:"center",
                            textShadow:"0 0 2px #000"
                          }}>
                            <div style={{
                              fontWeight:700,
                              overflow:"hidden",
                              textOverflow:"ellipsis"
                            }}>{c.className}</div>
                            {c.location && (
                              <div style={{
                                fontSize:c.textSize,
                                overflow:"hidden",
                                textOverflow:"ellipsis"
                              }}>{c.location}</div>
                            )}
                          </div>
                          <div style={{fontSize:11,textAlign:"right",textShadow:"0 0 2px #000"}}>
                            {c.endTime}
                          </div>
                        </div>
                      );
                    })}
                  </React.Fragment>
                );
              })}
            </div>
          </div>
        </div>

        {/* Download */}
        <button
          className="mt-2 px-4 py-2 bg-purple-600 text-white rounded"
          onClick={downloadTimetable}
        >Download as Image</button>
        <div className="mt-2 text-xs text-gray-400">
          (Preview is scaled down. Final export full resolution.)
        </div>
      </div>)}

      {/* Modals */}
      <AddClassModal
        visible={showModal}
        onClose={()=>setShowModal(false)}
        onAdd={handleAddClass}
        days={selectedDays}
      />
      {showCropper && rawImage && device && (
        <BackgroundCropperModal
          imageSrc={rawImage}
          width={device.width}
          height={device.height}
          onCropComplete={url=>{
            setBackgroundImage(url);
            setShowCropper(false);
          }}
          onClose={()=>setShowCropper(false)}
        />
      )}
    </div>
  );
}
